package Day10_loops;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 5;
		
		do { 
			System.out.println("Hello");  // false condition
		}while(num <3);
		
		//while loop checks condition first 
		// Do while loop checks code first and then condition
		
		
		do {
			System.out.println("Hello"); // this prints hello non stop because condition is true 
		}while(num >3);
		
		
		
		
		
		
		
		
		
		
	}

}
