package Day10_loops;

public class InClassTask2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
//			Write a program to calculate the sum of all the number between 1-100 output =5050
		
		int sum =0;
		
		
		for (int i =1 ; i < 101 ; i++) {
		
			sum = sum + i;
		
			System.out.println(sum);
		}

		
		
	}

}
