package Day10_loops;

public class Review {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// We us to run the code more then one time (loop)
		// While loop we run to see if condition is true (if condition is true it will run) +1
		//DO loops prints out 1 even if its false
		
		int num =0;
		
		if (num <3) {
			num++; // 0 + 1 
		}
		System.out.println(num);
		
		//------------------while loop
		
		while (num<3) {
			System.out.println(num);
			num++;
			
		} 
		
		
		
		
		
		
		
		
	}

}
