package Day10_loops;

public class TaskNumber5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//print a java program to print sum of odd number between 1-100
		
		
          int sum= 0;
		
		for (int i=1 ; i <=100 ; i++) {
			if (i%2 ==1) {
			sum =sum +i;
			}
		}
			System.out.println(sum);
		
		
		
		
		
		
		
		
		
	}

}
