package day17_array;

public class ToSwapValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		
		int a = 4;
		int b = 20;
		int max = 2;

		// to swap the value

		if (a > max) {
			max = a;
		}
		if (b > max) {
			max = b;
		}
		System.out.println(max);

	}

}
